import * as React from 'react'
import './styles.module.css'

interface Props {
  key: string
}
interface UiConfig {
  chatBoxName: string
  model: string
  botBackgroundColor: string
  botTextColor: string
  containerWidth: number
  containerHeight: number
  embedCollection: string
}

export const SamiChatBox = ({ key }: Props) => {
  console.log('key', key)
  const [isShow, setIsShow] = React.useState(false)
  const [uiConfig, setUiConfig] = React.useState<UiConfig>()

  const getUiConfig = React.useCallback(async () => {
    const res = await fetch(`https://be-sami-law-dev.acom.vn/users/${key}`)
    const data = await res.json()
    setUiConfig(data.uiConfig)
  }, [])

  React.useEffect(() => {
    getUiConfig()
  }, [getUiConfig])

  if (!uiConfig) return null

  return (
    <div id='sami_chatbot'>
      <div
        id='sami_chatbot_btn'
        onClick={() => {
          setIsShow(!isShow)
        }}
      >
        <img src='https://file-services-tt.s3.amazonaws.com/1698714060.9769785sami-logo.webp' />
      </div>
      <div
        id='sami_chatbot_iframe'
        style={{
          width: '500px',
          height: '80vh',
          display: isShow ? 'block' : 'none',
          opacity: isShow ? 1 : 0
        }}
      >
        <iframe
          width='100%'
          height='100%'
          src='https://chat.sami-ai.vn/embed?id=e1521a1a-1f22-4673-91c4-806afe17e339'
          frameBorder='0'
        />
        <div
          id='sami_chatbot_close'
          onClick={() => {
            setIsShow(false)
          }}
        >
          <svg
            xmlns='http://www.w3.org/2000/svg'
            fill='none'
            viewBox='0 0 24 24'
            stroke='currentColor'
            style={{ width: '20px', height: '20px' }}
          >
            <path
              strokeLinecap='round'
              strokeLinejoin='round'
              d='M6 18L18 6M6 6l12 12'
            />
          </svg>
        </div>
      </div>
    </div>
  )
}
